services.dppopupcss:
.sp-dsgvo-popup-overlay,
.sp-dsgvo-cookie-overlay {
    position: fixed;
    left: 0;
    top: 0;
    width: 100%;
    height: 100%;
    background-color: rgba(34, 34, 34, .8);
    z-index: 9999999;
}

.sp-dsgvo-privacy-popup{
    position: absolute;
    left: 50%;
    top: 50%;
    transform: translate(-50%, -50%);
    width: 100%;
    max-width: 500px;
    /*height: calc(100% - 100px);*/
    font-family: 'Roboto', Roboto, Helvetica, "Helvetica Neue", Verdana, Arial, sans-serif;
    font-size: 22px;
    padding-bottom: 10px;
    padding-left: 5px !important;
    padding-right: 5px !important;
    line-height: normal;
    background-color: white;
    color: black;
    letter-spacing: normal;
    box-sizing: border-box;
}

@media (max-width: 320px)
{
    .sp-dsgvo-privacy-popup {
        max-width: 310px;
        font-size: 18px;
    }
}

@media (max-width: 540px) {

    .sp-dsgvo-privacy-popup {
        max-width: 360px;
        font-size: 20px;
       /* max-height: 635px;*/
    }

}

@media (min-width: 541px) {

    .sp-dsgvo-privacy-popup {
        max-width: 620px;
    }

}

.sp-dsgvo-popup-overlay.sp-dsgvo-overlay-hidden {
    display: none;
}

.sp-dsgvo-privacy-popup .sp-dsgvo-popup-top{
    padding: 20px 10px 10px 10px;
    box-sizing:  border-box;
}

.sp-dsgvo-privacy-popup .sp-dsgvo-popup-more-information-top
{
    padding: 10px;
    height: 50px;
    box-sizing:  border-box;
}

.sp-dsgvo-popup-close,
.sp-dsgvo-popup-more-information-close {
    height: 10px;
}

a.sp-dsgvo-popup-close,
a.close
{
    background: none !important;
    border:none !important;
}

.sp-dsgvo-popup-close svg{
    vertical-align: top;
}

.sp-dsgvo-popup-more-information-close svg{
