}

.sp-dsgvo-privacy-popup .sp-dsgvo-lang-wrapper {
    display: flex;
    justify-content: flex-end;
}

.sp-dsgvo-privacy-popup .dsgvo-lang-active  {
    cursor: pointer;
    font-size: 12px;
    font-weight: 600;
    display: flex;
    align-items: flex-start;
}

.sp-dsgvo-privacy-popup .sp-dsgvo-lang-active span  {
