}

@media (max-width: 540px) {
    .sp-dsgvo-privacy-popup .sp-dsgvo-lang-active svg  {
        margin-top: 5px;
        vertical-align: top;
    }
}

.sp-dsgvo-privacy-popup .sp-dsgvo-lang-active svg line  {
