.sp-dsgvo .progress {
    display: -webkit-box;
    display: -ms-flexbox;
    display: flex;
    height: 10px;
    overflow: hidden;
    font-size: 0.703125rem;
    background-color: #ccc;
    border-radius: 0px;
}
.sp-dsgvo .progress-bar-animated {
    -webkit-animation: progress-bar-stripes 1s linear infinite;
    animation: progress-bar-stripes 1s linear infinite;
}
.sp-dsgvo .progress-bar-striped {
    background-image: linear-gradient(45deg, rgba(255,255,255,0.15) 25%, transparent 25%, transparent 50%, rgba(255,255,255,0.15) 50%, rgba(255,255,255,0.15) 75%, transparent 75%, transparent);
    background-size: 1rem 1rem;
}
.sp-dsgvo .progress-bar {
    display: -webkit-box;
    display: -ms-flexbox;
    display: flex;
    -webkit-box-orient: vertical;
    -webkit-box-direction: normal;
    -ms-flex-direction: column;
    flex-direction: column;
    -webkit-box-pack: center;
    -ms-flex-pack: center;
    justify-content: center;
    color: #fff;
    text-align: center;
    white-space: nowrap;
    background-color: #999;
    -webkit-transition: width 0.6s ease;
    transition: width 0.6s ease;
    font-size: 12px;
    font-weight: 500;
}

@-webkit-keyframes progress-bar-stripes {
    from {
        background-position: 1rem 0;
    }
    to {
        background-position: 0 0;
    }
}

@keyframes progress-bar-stripes {
    from {
        background-position: 1rem 0;
    }
    to {
        background-position: 0 0;
    }
}


/* cookie notice */


#cookie-notice-blocker {
    position: fixed;
    display: block;
    width: 100%;
    height: 100%;
    top: 0;
    left: 0;
    right: 0;
    bottom: 0;
    background-color: rgba(0,0,0,0.5);
    z-index: 9999
}

#cookie-notice {
    display: none;
    position: fixed;
    width: 100%;
    box-sizing: border-box;
    height: auto;
    z-index: 100000;
    font-family: 'Roboto', Roboto, Helvetica, "Helvetica Neue", Verdana, Arial, sans-serif;
    font-size: 13px;
    line-height: 20px;
    left: 0;
    text-align: center;
    background-color: #333;
    color: #fff
}

#cn-notice-text p
{
    margin-top: 0;
    margin-bottom: 0;
}

@media only screen and (max-width: 375px) {
    #cookie-notice {
        height:auto !important
    }
}

.cookie-notice-container {
    padding: 10px;
    text-align: center;
    width: 100%;
    display: block
}

@media only screen and (max-width: 576px) {
    .cookie-notice-container {
        text-align:center
    }

    #cn-notice-text {
        display: block
    }
}

.cookie-notice-container #cn-notice-icon {
    width: 25px;
    vertical-align: middle;
    margin-right: 5px;
    float: left
}

.cn-shadow-top {
    box-shadow: 0 -5px 5px 0 #e4e4e4
}

.cn-top {
    top: 0
}

.cn-bottom {
    bottom: 0
}

#cookie-notice .button {
    margin-left: 10px
}

#cookie-notice .button.button-default {
    line-height: 18px;
    padding: 2px 20px;
    background-image: none;
    -moz-box-sizing: border-box;
    border-radius: 2px;
    border-style: solid;
    border-width: 1px;
    cursor: pointer;
    display: inline-block;
    font-size: 12px;
    font-style: normal;
    text-decoration: none;
    text-transform: uppercase;
    white-space: nowrap;
    outline: 0;
    width: fit-content;
    height: fit-content
}

@media only screen and (max-width: 576px) {
    #cookie-notice .button.button-default {
        display:block;
        width: 100%;
        text-align: center;
        margin: 5px 0 0 0;
        padding: 5px
    }
}


