// ***************************************************************************    
// start legal web code
//
document.addEventListener('DOMContentLoaded', function() {

        /*
        $(document).on('click', '#popup_accept_button', function (event) {
            event.preventDefault();

            var $selectedGoogleFonts = $("select[name='google-fonts'] option:selected").text();

            if ($selectedGoogleFonts === 'Yes') {
                var $googleFonts = $(document).find('link[data-href]');

                $.each($googleFonts, function () {
                    $(this).attr('href', $(this).attr('data-href')).removeAttr('data-href');
                });
            }
        });
         */
        
        // check if notice is active and make body higher so that popup dont is in front of bottom links
        window.addEventListener('scroll', function() {
            var notice = document.querySelector('#cookie-notice');

            if ((notice.offsetWidth > 0 && notice.offsetHeight > 0) == false) return;
            const noticeStyle = getComputedStyle(notice);
            var height = parseInt(noticeStyle.height);//+ 10;
            if(window.scrollY + window.innerHeight > document.body.clientHeight - (height+20)) {
                document.body.style.paddingBottom = height+'px';
            } else
            {
                document.body.style.paddingBottom = 'inherit';
            }
        });

    });




    'use strict';

    var scrollBar = null;
    var scrollBarMoreInformation = null;


    /**
     * Language switcher click (expand/collapse)
     */
    function preparePopupLangSwitcher() {



        addClickTapTouchStartHandler('.sp-dsgvo-lang-active', function(item) {
            document.querySelectorAll('.sp-dsgvo-lang-dropdown').forEach(function(dd){
                if (dd.classList.contains('active')) dd.classList.remove('active'); else dd.classList.add('active');
            });
        }, true);

/*
        var $switcher = $('.sp-dsgvo-popup-language-switcher');
        $(document).on('click tap touchstart ', function (e) {
            if (!$switcher.is(e.target) && $switcher.has(e.target).length === 0) {
                $('.sp-dsgvo-lang-dropdown').removeClass('active');
            }
        });
        */

    }

    /**
     * Adds Simplebar js scrollbar
     */
    function prepareScrolling() {
        var content = document.querySelector('#sp-dsgvo-privacy-content-category-content');

        if (content.children.length > 0) {
            scrollBar = new SimpleBar(document.querySelector('#sp-dsgvo-privacy-content-category-content'), {
                autoHide: false
            });
        }
        var moreInformation = document.querySelector('.sp-dsgvo-popup-more-information-content');

        if (moreInformation.children.length > 0) {
            scrollBarMoreInformation = new SimpleBar(document.querySelector('.sp-dsgvo-popup-more-information-content'), {
                autoHide: false
            });
        }

    }


    function preparePopupShowLinks()
    {
        addClickTapTouchStartHandler('.sp-dsgvo-show-privacy-popup', function(item) {
            showPopup();
        }, true);

        // links of privacy policy
        addClickTapTouchStartHandler('.lwb-ppsp', function(item) {
            showPopup();
        }, true);

        // links of privacy policy
        addClickTapTouchStartHandler('.sp-dsgvo-navigate-privacy-policy', function(item) {
            window.location = spDsgvoGeneralConfig.privacyPolicyPageUrl;
        }, true);

        // action for enabling embedded content
        addClickTapTouchStartHandler('.sp-dsgvo-direct-enable-popup', function(item) {
            var slug = item.dataset.slug
            if (slug == null || slug == '') return;

            enableEmbeddingByPlaceholderClick(slug);
        }, true);
    }


    function preparePopupOverlay()
    {
        addClickTapTouchStartHandler('.sp-dsgvo-popup-overlay', function(item, event) {

            var clicked = event.target;  //get the element clicked
            var a = clicked;
            var parents = [];
            while (a) {
                parents.unshift(a);
                a = a.parentNode;
            }
            parents = parents.reverse();

            if (clicked.classList.contains('sp-dsgvo-privacy-popup') || checkIfOneItemHasClass(parents, 'sp-dsgvo-privacy-popup')) {
                return;  //click happened within the popup, do nothing here
            } else {  // click was outside the popup, so close it

                if (getAndValidateCookie() == false) // if no cookie exists (first visit) a click is like a dismiss all, otherwise a cancel/close
                {
                    handlePopupButtonAction('dismissAll');
                    closePopup(true);
                } else {
                    closePopup(false);
                }
            }

        }, false);
    }

    function checkIfOneItemHasClass(items, className)
    {
        for (var i =0; i <items.length; i++)
        {
            if (items[i].classList && items[i].classList.contains(className)) return true;
        }

        return false;
    }

    function preparePopupActionButtons() {
        // x button
        addClickTapTouchStartHandler('.sp-dsgvo-popup-close', function(item) {
            if (getAndValidateCookie() == false) // if no cookie exists (first visit) a click is like a dismiss all, otherwise a cancel/close
            {
                handlePopupButtonAction('dismissAll');
                closePopup(true);
            } else {
                closePopup(false);
            }
        }, true);



        // dismiss all button
        addClickTapTouchStartHandler('.sp-dsgvo-privacy-btn-accept-nothing', function(item) {
            handlePopupButtonAction('dismissAll');
            closePopup(false);
        }, true);

        // accept selected button
        addClickTapTouchStartHandler('.sp-dsgvo-privacy-btn-accept-selection', function(item) {

            handlePopupButtonAction('acceptSelected');
            closePopup(false);
        }, true);


        // accept all button
        addClickTapTouchStartHandler('.sp-dsgvo-privacy-btn-accept-all', function(item) {

            handlePopupButtonAction('acceptAll');

            closePopup(false);
        }, true);
    }

    function preparePopupSwitches()
    {
        // uncheck all$
        var cbs = document.querySelectorAll('.sp-dsgvo-switch-integration:enabled');
        cbs.forEach(function(cb) {cb.checked = false;});

        // disable neccessary switches
        var cbs = document.querySelectorAll('#sp-dsgvo-switch-category-necessary_services');
        if (cbs != null && cbs.length > 0)
        {
            cbs.forEach(function(cb) {
                //cb.setAttribute('checked','checked');
                cb.checked = true;
                cb.setAttribute('disabled','disabled');
            });
        }
        cbs = document.querySelectorAll('input[type=checkbox][data-category="necessary_services"]');
        if (cbs != null && cbs.length > 0)
        {
            cbs.forEach(function(cb) {
                cb.checked = true;
                //cb.setAttribute('checked','checked');
                cb.setAttribute('disabled','disabled');
            });
        }

        document.querySelectorAll('.sp-dsgvo-switch-integration').forEach(function(input) {

            input.addEventListener('change',function (event) {

                var item = event.target;
                var category = item.getAttribute('data-category');
                
                checkIfAllOfGroupHaveSameState(category, item.checked);
                checkForIntegrationModificationsAndSetColorToButtons();
            });          
        });

        var enabledIntegrations = getEnabledIntegrationsFromCookie();

        enabledIntegrations.forEach(function(integration) {

            // check if the script container exists. if not create it to add script afterwards
            if (document.querySelector('#sp-dsgvo-switch-integration-'+integration) != null) {
                document.querySelector('#sp-dsgvo-switch-integration-'+integration).checked = true;
            }
        });
    }

    function checkForIntegrationModificationsAndSetColorToButtons()
    {
        var array1 = getEnabledIntegrationsFromCookie();
        var array2 = [];
        document.querySelectorAll('.sp-dsgvo-switch-integration:checked').forEach(function(i){
            array2.push(i.dataset.slug);
        });

        var equal = array1.length === array2.length && array1.every(function(value, index) { return value === array2[index]});

        if (equal == false)
        {
            document.querySelector('.sp-dsgvo-privacy-btn-accept-selection').classList.add('green');
            document.querySelector('.sp-dsgvo-privacy-btn-accept-selection').classList.remove('grey');
        } else
        {
            document.querySelector('.sp-dsgvo-privacy-btn-accept-selection').classList.remove('green');
            document.querySelector('.sp-dsgvo-privacy-btn-accept-selection').classList.add('grey');
        }
    }

    function checkIfAllOfGroupHaveSameState(categorySlug, cbState)
    {
        var countSameState = 0;
        var countIntegrationsOfCategory = 0;

        // get all sp-dsgvo-switch-integration with slug and set same check value
        document.querySelectorAll('input[data-category="' + categorySlug + '"]').forEach(function (item) {
            if (item.checked == cbState) countSameState++;
            countIntegrationsOfCategory++;           
        });

        if (countSameState == countIntegrationsOfCategory)
        {
            document.querySelector('#sp-dsgvo-switch-category-' + categorySlug).checked = cbState;
        } else {
            document.querySelector('#sp-dsgvo-switch-category-' + categorySlug).checked = false;
        }
    }

    function closePopup(closedOnFirstVisit) {
        var overlay = document.querySelector('.sp-dsgvo-popup-overlay');
        if (overlay.children.length > 0) {
            fadeOut(overlay);
            overlay.classList.add('sp-dsgvo-overlay-hidden');

            /* showNoticeOnClose could be
              - no
              - on_no_selection
              - when_popup_hidden
            */
            if ((spDsgvoGeneralConfig.showNoticeOnClose == 'on_no_selection' && closedOnFirstVisit) 
                || (spDsgvoGeneralConfig.showNoticeOnClose == 'when_popup_hidden')) {
                showNotice();
            }
        }
    }

    function showPopup() {

        preparePopupSwitches();
        checkPopupGroupSwitchesStates();

        var overlay = document.querySelector('.sp-dsgvo-popup-overlay');

        if (overlay.children.length > 0) {
            closeNotice();
            fadeIn(overlay);
            overlay.classList.remove('sp-dsgvo-overlay-hidden');

        }
    }

    function showNotice()
    {
        var cnDomNode = document.querySelector('#cookie-notice');


        if (spDsgvoGeneralConfig.noticeHideEffect === 'fade') {
            fadeIn(cnDomNode);
        } else if (spDsgvoGeneralConfig.noticeHideEffect === 'slide') {
            slideDown(cnDomNode);
        } else {
            cnDomNode.style.display = 'flex';
        }

        document.body.classList.add('cookies-not-accepted');
    }

    function closeNotice()
    {
        var cnDomNode = document.querySelector('#cookie-notice');
        // if not visible no need to close
        if ((cnDomNode.offsetWidth > 0 && cnDomNode.offsetHeight > 0) == false) return;

        if (spDsgvoGeneralConfig.noticeHideEffect === 'fade') {
            fadeOut(cnDomNode, function () {
                hide('#cookie-notice');
                hide('#cookie-notice-blocker');
                document.body.classList.remove('cookies-not-accepted');
            });
        } else if (spDsgvoGeneralConfig.noticeHideEffect === 'slide') {
            slideUp(cnDomNode, function () {
                hide('#cookie-notice');
                hide('#cookie-notice-blocker');
                document.body.classList.remove('cookies-not-accepted');
            });
        } else {
            hide('#cookie-notice');
            hide('#cookie-notice-blocker');
            document.body.classList.remove('cookies-not-accepted');
        }
    }

    function checkCookieAndShowPopupOrNoticeIfNeeded()
    {
        if (spDsgvoIntegrationConfig == null) return;

        if (spDsgvoIntegrationConfig.length == 0 && spDsgvoGeneralConfig.forceCookieInfo == 0) return;

        // legacy
        if (Array.isArray(spDsgvoGeneralConfig.privacyPolicyPageUrl) == false) {
            if (window.location.href.indexOf(spDsgvoGeneralConfig.privacyPolicyPageUrl) >= 0 ||
                window.location.href.indexOf(spDsgvoGeneralConfig.imprintPageUrl) >= 0) {
                // dont show popup or notice at imprint or policy page
                return;
            }
        }

        let idx = '';
        for (idx in spDsgvoGeneralConfig.privacyPolicyPageUrl) {
            if (window.location.href.indexOf(spDsgvoGeneralConfig.privacyPolicyPageUrl[idx]) >= 0 )
            {
                // dont show popup or notice at imprint or policy page
                return;
            }
        }

        for (idx in spDsgvoGeneralConfig.imprintPageUrl) {
            if (window.location.href.indexOf(spDsgvoGeneralConfig.imprintPageUrl[idx]) >= 0 )
            {
                // dont show popup or notice at imprint or policy page
                return;
            }
        }

        if (getAndValidateCookie() == false)
        {
            if (spDsgvoGeneralConfig.initialDisplayType == 'cookie_notice') {
                showNotice();
            } else if (spDsgvoGeneralConfig.initialDisplayType == 'policy_popup') {
                showPopup();
            }
        }
    }

