  // helper methods  
    function hide(elementSelector)
    {
        var els = document.querySelectorAll(elementSelector);
        if (els == null) return;

        els.forEach(function(item) {
            const style = window.getComputedStyle(item);
            displayCache[elementSelector] = style.display;
            item.style.display = 'none';
        });

    }

    function show(elementSelector)
    {
        var els = document.querySelectorAll(elementSelector);
        if (els == null) return;

        els.forEach(function(item) {
            item.style.display = displayCache[elementSelector] != null && displayCache[elementSelector] != '' ? displayCache[elementSelector] : 'block';
        });
    }

    function attr(elementSelector, attrName, attrValue)
    {
        var els = document.querySelectorAll(elementSelector);
        if (els == null) return;

        els.forEach(function(item) {
            item.setAttribute(attrName, attrValue);
        });
    }

    function prop(elementSelector, propName, propValue)
    {
        var els = document.querySelectorAll(elementSelector);
        if (els == null) return;

        els.forEach(function(item) {
            item[propName] = propValue;
        });
    }

    function addClickTapTouchStartHandler(elementSelector, callbackMethod, cancelBubble){
        var items = document.querySelectorAll(elementSelector);
        items.forEach( function(item) {

                ['click', 'tap','touchstart'].forEach(function(listener) {
                    item.addEventListener(listener, function(event) {

                        if (cancelBubble) {
                            event.preventDefault();
                            event.stopPropagation();
                        }

                        callbackMethod(item, event);
                    });
                });
            }
        );
    }

    function fadeOut(el, completedCallback){
        el.style.opacity = 1;

        (function fade() {
            if ((el.style.opacity -= .1) < 0) {
                el.style.display = "none";
                if (completedCallback != null) completedCallback();
            } else {
                requestAnimationFrame(fade);
            }
        })();
    };

    function fadeIn(el, display){
        el.style.opacity = 0;
        el.style.display = display || "block";

        (function fade() {
            var val = parseFloat(el.style.opacity);
            if (!((val += .1) > 1)) {
                el.style.opacity = val;
                requestAnimationFrame(fade);
            }
        })();
    };

    function slideDown(elem) {
        elem.style.maxHeight = '1000px';
        // We're using a timer to set opacity = 0 because setting max-height = 0 doesn't (completely) hide the element.
        elem.style.opacity   = '1';
    }

    function slideUp(elem, callbackHandler) {
        elem.style.maxHeight = '0';
        once( 1, function () {
            elem.style.opacity = '0';
        });
        if (callbackHandler != null) callbackHandler();
    }

    function addScriptToDom(slug, scriptText, target, checkForScriptNode){

        if (checkIfIntegrationHasBeenEnabled(slug)) return;

        /*
        if (checkForScriptNode == true)
        {
            scriptText = scriptText.replace(/<script>/ig,'');
            scriptText = scriptText.replace(/<\/script>/ig,'');
        }
        */

       if (scriptText.indexOf('<script>') < 0) scriptText = '<script>' + scriptText;
       if (scriptText.indexOf('<\/script>') < 0) scriptText = scriptText + '<\/script>';


       var tempContainer  = document.createElement('div');
       tempContainer.insertAdjacentHTML('afterbegin',scriptText);

       var allScripts = tempContainer.querySelectorAll('script');
       var allNoScripts = tempContainer.querySelectorAll('noscript');

        if (target != null) {
           
            allScripts.forEach(function(scriptNode) {
				var script = document.createElement("script");
				script.async = scriptNode.async;
				if (scriptNode.src != null && scriptNode.src != '') script.src = scriptNode.src;
				script.innerHTML = scriptNode.innerHTML;
				target.appendChild(script);		
			});
			
			allNoScripts.forEach(function(scriptNode) {
				var script = document.createElement("noscript");				
                script.innerHTML = scriptNode.innerHTML;
				target.appendChild(script);		
			});

            runningIntegrations.push(slug);
        }
    
    }

    /**
     * Call once after timeout
     * @param  {Number}   seconds  Number of seconds to wait
     * @param  {Function} callback Callback function
     */
    function once (seconds, callback) {
        var counter = 0;
        var time = window.setInterval( function () {
            counter++;
            if ( counter >= seconds ) {
                callback();
                window.clearInterval( time );
            }
        }, 400 );
    }

